catalog_script_client.script:
function onLoad() {
    EVE_ITSHOP_getUserLocation();
}
/**
 * Check User location and grant permission for request accordingly
 * Global function callable from other client scripts
 */
EVE_ITSHOP_getUserLocation = function() {
    var dataSetSetupAJAX = new GlideAjax('x_infte_eve_cat_c.EVE_CAT_DataSetAJAX');
    dataSetSetupAJAX.addParam('sysparm_name', 'getDataSetScripted');
    dataSetSetupAJAX.addParam('sysparm_data_set', 'SIM card request Check user location');
    dataSetSetupAJAX.addParam('sysparm_language', 'en');
    dataSetSetupAJAX.addParam('sysparm_data_query', JSON.stringify({
        userID: g_user.userID
    }));
    dataSetSetupAJAX.getXMLAnswer(checkLocation);
};


/**
 * check user location, make request unable for non Germany located users
 */
function checkLocation(response) {
    var checkObject = JSON.parse(response);
    var check = '';
    check = checkObject[0].value;
    if (check == 'false') {
		g_form.setMandatory('requested_for', false);
        g_form.setDisplay('requested_for', false);
        g_form.clearOptions('action');
        g_form.addOption('action', '', '-- None --');
		g_form.showFieldMsg('action',getMessage('sim_card_request.location_error'), 'error');
        g_form.addErrorMessage(getMessage('sim_card_request.location_error'), 'error');
    }
}
