catalog_script_client.script:
function onLoad() {

    var formStructure = {
        "new": [
            "container_begin_3",
            "container_label_3",
            "cost_center",
            "container_begin_4",
            "card_type",
            "contract_type",
            "service_provider",
            "tariff",
            "multi_sim",
            "justification",
            "hauspost_code"
        ],
        "change": [
            "change_options",
            "container_begin_3",
            "container_label_3",
            "cost_center",
            "container_begin_5",
            "existing_number",
            "container_begin_6",
			"container_begin_7"
        ],
        "service": [
            "service_options",
            "container_begin_3",
            "container_label_3",
            "cost_center",
            "container_begin_5",
            "container_label_5",
            "existing_number",
            "container_begin_7"
        ],
        "cancel": [
            "container_begin_3",
            "container_label_3",
			"cost_center",
            "container_begin_5",
            "container_label_5",
			"existing_number",
			"existing_id"
        ],
        "other": [
            "request_specification"
        ],
    };

    hideVariables();
    buildForm(formStructure[g_form.getValue('variables.action')]);

    //add variables for fulfiller in case of action new
    var newVariablesArr = ['phone_number', 'sim_id', 'sim_group'];
    for (var i = 0; i < newVariablesArr.length; i++) {
        if (g_form.getValue('state') < 3 && g_form.getValue('variables.action') == "new") {
            g_form.setReadOnly(newVariablesArr[i], false);
            g_form.setMandatory(newVariablesArr[i], true);
        } else {
            g_form.setReadOnly(newVariablesArr[i], true);
            g_form.setMandatory(newVariablesArr[i], false);
        }
    }

}

/**
 * Displaying variables
 * @param {Array} variablesArr - array of fields to be displayed
 */
function buildForm(variablesArr) {
    for (var i = 0; i < variablesArr.length; i++) {
        g_form.setDisplay(variablesArr[i], true);
    }
}
/**
 * Hiding all fields
 */
function hideVariables() {
    // Common always shown fields for all actions 
    var actionVariablesArr = ['container_begin_1', 'action', 'requested_for'];
    for (var i = 0; i < g_form.nameMap.length; i++) {
        var name = g_form.nameMap[i].prettyName;
        if (actionVariablesArr.indexOf(name) == -1) {
            g_form.setDisplay(name, false);
        }
    }
}
