catalog_script_client.script:
function onSubmit() {

	var validPhone = true;
	var validTarget = true;
	var validSim = true;
	var validNewSim = true;

	// validate values only if values have been entered (fields in use)
	var phone = g_form.getValue('existing_number');
	if (phone != '')
		validPhone = g_scratchpad.validatePhone(phone);
		var target = g_form.getValue('target_number');
	if (target != '')
		validTarget = g_scratchpad.validatePhone(target);
		var simId = g_form.getValue('existing_id');
	if (simId != '')
		validSim = g_scratchpad.validateSimId(simId);
		var newSim = g_form.getValue('new_sim_id');
	if (newSim != '')
		validNewSim = g_scratchpad.validateSimId(newSim);

	// everything checks out
	if(validPhone && validTarget && validSim && validNewSim){
		return true;
	// something is not valid, show message and abort submission
	} else {
		if(!validPhone || !validTarget)
			g_form.addErrorMessage(getMessage('sim.validation.invalid_phone_number'));
			if(!validSim || !validNewSim)
			g_form.addErrorMessage(getMessage('sim.validation.invalid_sim_id'));

		return false;
	}
}
