sys_script_client.script:
// field coloring
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }

    if (newValue === 'completed') {
        control.style.backgroundColor = '#90ee90';
        return;
    }

    if (newValue === 'in_progress') {
        control.style.backgroundColor = 'yellow';
        return;
    }

    if (newValue === 'not_reached') {
        control.style.backgroundColor = '#FF6347';
        return;
    }

    control.style.backgroundColor = '';
	}
