sys_script_include.script:
(function(language, dataQuery, source /* returns source environment legal entity name. It can be 'mbag' or 'dtag' or 'everest'.*/ ) {
	/**
	 * Function queries and returns personal information on user
	 * @param {json} dataQuery - data containing user_name of the requested for
	 * @return {string} cost center associated with the given user sys id
	 */

    var userLocation = "";
	var validated = false;
    var userID = JSON.parse(dataQuery).userID;
    //  Check if the user exist in personal data table
    var userGR = new x_infte_eve_pdata.EVE_PDATA_Utils().getUserByIdentifier(userID);

    if (userGR != null) {
        //get user location
		var pdata = new x_infte_eve_pdata.EVE_PDATA_Utils().getPersonalDataByUser(userGR, ['location'], 'SIM Card request', 'user location required');
        if (pdata['location'] != '') {
			var locationGR = new GlideRecord('cmn_location');
            if (locationGR.get(pdata['location'])) {
                userLocation = locationGR.getValue('country');
            }
        }
    }
	if (userLocation == "Germany") {
		validated = true;
	}

    return validated;
})(language, dataQuery, source);
